import React from "react";

class TodoForm extends React.Component{
    constructor(){
        super();
        this.state = {
            item: ""
        };
    }

    changeHandler = event => {
        this.setState({
            [event.target.name]: event.target.value 
        });
    }

    submitHandler = event => {
        event.preventDefault();
        event.target.reset(); // Resets value to blank
        this.props.addItem(this.state.item); // addItem function located in App.js
        const test = () => {
            if(window.innerWidth < 960){
                return "Enter task"

            } 
            else{
                return "Whatcha gotta do?"
            }
        }

        console.log(test)
    }

    //placeholderText = text => window.innerWidth < 960 ? text = "Enter task" : this.placeholderText = "Whatcha gotta do?";

    render(){
        return(

            <form className="form" onSubmit={this.submitHandler}>
                <input
                    className="inputField"
                    placeholder={() => "ramon", console.log(this.placeholderText)}
                    type="text"
                    value={this.item}
                    name="item"
                    onChange={this.changeHandler}
                />

                <button className="addBtn">Add</button>
            </form>

        );
    }
}

export default TodoForm;
